import React from 'react';
import logo from './logo.svg';
import './App.css';
// import FixedMenuLayout from './FixedMenuLayout'
import HomepageLayout from './HomepageLayout'
import NavBar from './NavBar'
import LoginSignupContainer from  './LoginSignupContainer'
import MyProfile from './MyProfile'
import SignupForm from './SignupForm'
// import LoginForm from './LoginForm'
import { BrowserRouter as Router, Route } from "react-router-dom"

// import { getQueriesForElement } from '@testing-library/react';

import { getQueriesForElement } from '@testing-library/react';
import PetCard from './PetCard'

//state: isUserLogedIn: null

const ownersURL = "http://localhost:3000/owners"
const petsURL = "http://localhost:3000/pets"
const notesURL = "http://localhost:3000/notes"





//Fetch in here

//function setUser()

//function getUser()

//function createUser()

////function logOutUser()



class App extends React.Component {
  State = { 
    owners:[],
    isLoggedIn: false,
    User: {}
  }

  renderOwnersProfile = (firstName) => {
    console.log(firstName)
    fetch(ownersURL)
    .then(res => res.json())
    .then(owners => console.log(owners))
  }

  onLogInUser = (state) => {
    this.setState({...this.state, isLoggedIn: true})
    console.log(state.name)
    this.renderOwnersProfile(state.name)
    // this.setState(prevState => {
    //   return { o}
    // })
  }





  adduser = owner => {
    this.setState(prevState => {
      return {
        owners: [...prevState.owners, owner]
      }
    }, () => this.postOwner(owner))
  }

  postOwner = (owner) => {
    fetch(ownersURL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(owner)
    }).then(res => res.json())
      .then(data => console.log(data))
      //send to owner profile 
  }


  handleOnLogIn = () => {
    console.log("ello mate")
  }

  render() {
    return (
      <div>
        <Router>
          <NavBar />{

          <Route
            path="/"
            exact
            render={() => <HomepageLayout />}
          />}
                {
           <Route
          path="/login"
          exact
          render={() => 
          <LoginSignupContainer onLogInUser={this.onLogInUser}/>}
          />}

            {
        <Route
        path="/signup"
        exact
        render={()=> <SignupForm onAddUser={this.adduser}/>}
        />}

            {
        <Route
          path="/signup"
          exact
          render={() => 
          <LoginSignupContainer />}
          />}


          {
        <Route
        path="/profile"
        exact
        render={() => <MyProfile />}
        />
          }


        </Router>
      </div>
    );
  }
}

export default App;
