import React from 'react'
import LoginForm from './LoginForm'
import PetCard from './PetCard.js'
import PetForm from './PetForm'
import LoginSignupContainer from './LoginSignupContainer.js'
const petsURL = "http://localhost:3000/pets"

class PetsContainer extends React.Component {

  constructor() {
    super()
    this.state = {
      currentUserPets: this.props.user.pets
    }
  }


  addPet = pet => {
    this.setState(prevState => {
      return {
        currentUserPets: [...prevState.currentUserPets, pet]
      }
    }, () => this.postPet(pet))
  }


  //iterate over all pets for this specific owner and create a PetCard for them
  postPet = (pet) => {
    console.log(pet)
    fetch(petsURL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify({
        pet: { ...pet, owner_id: this.props.user.id }
      })
    }).then(res => res.json())
      .then(() => this.setState({ currentUserPets: [...this.state.currentUserPets, pet] }))
    // this.setState({currentUserPets: this.state.currentUserPets[0]})
  }

  // Create Pet Cards:
  everyPet = () => {
    if (this.props.user) {
    console.log(this.props.pets)
    return this.state.currentUserPets.map(pet => {
      return <PetCard pet={pet} />
    }
    )
   } else {
     return <LoginForm />
   }
  }



  render() {

    return (
      <div className="ui grid container">
        {/* /* {console.log(this.props.user)} */}
        {/* {console.log(this.props.pets)} */}
        {/* <button class="ui button" >Add a Pet</button> */}
        <PetForm addPet={this.addPet} user={this.props.user} />
        {/* everyPet() */}

        {/* <PetCard/> */}
        {this.everyPet()}
      </div>
    )
  }

}
export default PetsContainer
