import React from 'react';
import { Button, Form, Header, Image, Modal } from 'semantic-ui-react'
const INITIAL_STATE = {
    name: "",
    image: "",
    anmial_type: "",
    age: "",
//    owner_id: ""
  };
class PetForm extends React.Component {
//testing modal click functionality
  state = { open: false }
  closeConfigShow = (closeOnEscape, closeOnDimmerClick) => () => {
    this.setState({ closeOnEscape, closeOnDimmerClick, open: true })
  }
  close = () => this.setState({ open: false })
//-------------------------------------------
    state = INITIAL_STATE;
    handleChange = e => {
          this.setState({ [e.target.name]: e.target.value })
      };
      handleFormSubmit = e => {
        // debugger;
        e.preventDefault();
        this.props.postPet(this.state);
        this.setState(INITIAL_STATE);
      };
  render() {
    //testing modal:
    const { open, closeOnEscape, closeOnDimmerClick } = this.state
    return(
<div>
        <Button onClick={this.closeConfigShow(false, true)}>
          Add a Pet
        </Button>
        <Modal
          open={open}
          closeOnEscape={closeOnEscape}
          closeOnDimmerClick={closeOnDimmerClick}
          onClose={this.close}
        >
          <Modal.Header>Add a Pet</Modal.Header>
          <Modal.Content>
            <p>Please add info about your pet</p>
          </Modal.Content>
          <form className="ui form" onSubmit={this.handleFormSubmit}>
      <div className="field">
        <label>Name</label>
        <input type="text" name="name" value={this.state.name} onChange={this.handleChange} placeholder="Name" />
      </div>
      <div className="field">
        <label>Profile Image</label>
        <input type="text" name="image" value={this.state.image} onChange={this.handleChange} placeholder="Image" />
      </div>
      <div className="field">
        <label>Anmial Type</label>
        <input type="text" name="anmial_type" value={this.state.anmial_type} onChange={this.handleChange} placeholder="Animal Type" />
      </div>
      <div className="field">
        <label>Age</label>
        <input type="text" name="age" value={this.state.age} onChange={this.handleChange} placeholder="age" />
      </div>
      {/* <button type="submit" className="ui button">Submit</button> */}
    </form>
          <Modal.Actions>
            <Button onClick={this.close} negative>
            Close
            </Button>
            <Button
              onClick={this.handleFormSubmit}
              positive
              labelPosition='right'
              icon='checkmark'
              content='Submit'
            />
          </Modal.Actions>
        </Modal>
      </div>
    )
  }
}
export default PetForm





