  constructor() {
    super()
    this.state ={
      currentUserPets:[]
    }
  }


  addPet = pet=> {
    this.setState(prevState => {
      return {
        currentUserPets: [...prevState.currentUserPets, pet]
      }
    }, () => this.props.postPet(pet))
